import React from 'react';
import * as go from 'gojs';
import { ReactDiagram } from 'gojs-react';

import './App.css';  // contains .diagram-component CSS

// ...

/**
 * Diagram initialization method, which is passed to the ReactDiagram component.
 * This method is responsible for making the diagram and initializing the model and any templates.
 * The model's data should not be set here, as the ReactDiagram component handles that via the other props.
 */

const json = [
  {
    "id": "MS1",
    "calls": 3,
    "requests": 1,
    "puntos_historia": 19,
    "complejidad": 19.00,
    "acoplamiento": {
      "AIS": 2.00,
      "ADS": 0.00,
      "SIY": 0.00
    },
    "cohesion": {
      "lack": 2.00,
      "grade": 0.67
    },
    "similitud_semantica": 0.70,
    "historias": [
      {
        "id": "hu1",
        "dependencias": ["hu3", "hu4"]
      },
      {
        "id": "hu2",
        "dependencias": ["hu4"]
      },
      {
        "id": "hu5",
        "dependencias": []
      }
    ]
  },
  {
    "id": "MS2",
    "calls": 1,
    "requests": 1,
    "puntos_historia": 9,
    "complejidad": 9.00,
    "acoplamiento": {
      "AIS": 1.00,
      "ADS": 1.00,
      "SIY": 0.00
    },
    "cohesion": {
      "lack": 2.00,
      "grade": 0.67
    },
    "similitud_semantica": 0.70,
    "historias": [
      {
        "id": "hu3",
        "dependencias": ["hu1"]
      }
    ]
  },
  {
    "id": "MS3",
    "calls": 0,
    "requests": 2,
    "puntos_historia": 23,
    "complejidad": 23.00,
    "acoplamiento": {
      "AIS": 0.00,
      "ADS": 2.00,
      "SIY": 0.00
    },
    "cohesion": {
      "lack": 2.00,
      "grade": 0.67
    },
    "similitud_semantica": 0.73,
    "historias": [
      {
        "id": "hu4",
        "dependencias": []
      },
      {
        "id": "hu8",
        "dependencias": []
      }
    ]
  },
  {
    "id": "MS4",
    "calls": 4,
    "requests": 0,
    "puntos_historia": 15,
    "complejidad": 15.00,
    "acoplamiento": {
      "AIS": 0.00,
      "ADS": 2.00,
      "SIY": 0.00
    },
    "cohesion": {
      "lack": 2.00,
      "grade": 0.67
    },
    "similitud_semantica": 0.73,
    "historias": [
      {
        "id": "hu6",
        "dependencias": ["hu1", "hu4"]
      },
      {
        "id": "hu7",
        "dependencias": ["hu4"]
      },
      {
        "id": "hu9",
        "dependencias": ["hu3"]
      },
      {
        "id": "hu10",
        "dependencias": []
      },
      {
        "id": "hu11",
        "dependencias": []
      },
      {
        "id": "hu12",
        "dependencias": []
      },
      {
        "id": "hu13",
        "dependencias": []
      }/*,
          {
              "id": "hu14",
              "dependencias": []
          }/*,
          {
              "id": "hu15",
              "dependencias": []
          }/*,
          {
              "id": "hu16",
              "dependencias": []
          }*/
    ]
  }
]

function initDiagram() {
  const $ = go.GraphObject.make;
  // set your license key here before creating the diagram: go.Diagram.licenseKey = "...";
  const myDiagram = $(go.Diagram, {
    "undoManager.isEnabled": true, // Para hacer debug en consola del navegador
    contentAlignment: go.Spot.Center,  // Centra el contenido en el viewport (ventana gráfica)
    "toolManager.hoverDelay": 10,  // Qué tan rápido aparece el tooltip
    isReadOnly: false,  // No permite modificar
    //mouseOver: doMouseOver,  // Este manejador de eventos se define abajo
    //click: doMouseOver,  // Este manejador de eventos se define abajo
    model: new go.GraphLinksModel({
      linkKeyProperty: 'key'  // IMPORTANT! must be defined for merges and data sync when using GraphLinksModel
    })
  });

  myDiagram.groupTemplate = // Define una plantilla simple de grupos
    $(go.Group, "Vertical", { // 'Vertical' Pone el id del nodo abajo
      selectable: true,
      deletable: false, // No permite borrar el grupo
      //toolTip: myToolTip,
      copyable: false
    },
      new go.Binding("layout", "columnas", function (c) {
        return $(go.GridLayout, {
          wrappingColumn: c, // Determina cantidad de columnas
          cellSize: new go.Size(0, 0), // Determina ancho de columnas
          spacing: new go.Size(0, 0) // Determina espacio entre columnas
        });
      }),
      $(go.Panel, "Spot",
        $(go.Shape, "RoundedRectangle", {
          name: "MICROSERVICIO",
          strokeWidth: 4, // Ancho del borde
          stroke: null, // Sin borde
          fill: "rgba(125, 200, 120, .6)"
        },
          new go.Binding("width", "size"), // Asigna al ancho el valor de size
          new go.Binding("height", "size") // Asigna a la altura el valor de size
        ), $(go.TextBlock, "text", {
          alignment: new go.Spot(0.5, 0.23) // valores de 0,0 (izquierda,arriba) a 1,1 (derecha, abajo)
        },
          new go.Binding("text", "key") // Asigna al texto el valor de key
        ),
        $(go.Placeholder, { // Centra las HUs en el hexágono del microservicio
          padding: 1
        })
      )
    );

  myDiagram.nodeTemplate =
    $(go.Node, "Auto", { // 'Auto' Ajusta el tamaño del elemento según sus subelementos. 
      //locationSpot: go.Spot.Center,
      selectable: false,
      deletable: false // No permite borrar el nodo
    },
      $(go.Shape, "RoundedRectangle", {
        fill: "white"
      }),
      $(go.TextBlock, "text",
        new go.Binding("text", "key") // Asigna al texto el valor de key
      )
    );

  myDiagram.linkTemplate =
    $(go.Link, {
      curve: go.Link.Bezier,
      reshapable: true,
      deletable: false // No permite borrar la flecha
    },
      // No necesita guardar Link.points, por lo que no necesita TwoWay Binding en "puntos"
      new go.Binding("curviness", "curviness").makeTwoWay(),  // pero guarda "curvas" automáticamente
      $(go.Shape, { // Características del cuerpo de la flecha       
        strokeWidth: 2
      }),
      $(go.Shape, { // Características de la cabeza de la flecha  
        toArrow: "standard", // Forma de la cabeza de la flecha  
        stroke: "white" // Borde de la cabeza de flecha
      })
    );

  return myDiagram;
}

/**
 * This function handles any changes to the GoJS model.
 * It is here that you would make any updates to your React state, which is dicussed below.
 */

// render function...
function App() {


  let nodeDataArray = [];
  let linkDataArray = [];

  for (let i = 0; i < json.length; i++) {
    nodeDataArray = nodeDataArray.concat({
      acoplamiento_AIS: json[i].acoplamiento.AIS,
      acoplamiento_ADS: json[i].acoplamiento.ADS,
      acoplamiento_SIY: json[i].acoplamiento.SIY,
      cohesion_lack: json[i].cohesion.lack,
      cohesion_grade: json[i].cohesion.grade,
      columnas: Math.ceil(Math.sqrt(json[i].historias.length)),
      complejidad: json[i].complejidad,
      isGroup: true,
      key: json[i].id,
      puntos_historia: json[i].puntos_historia,
      size: 55 + (json[i].historias.length * 12),
      similitud_semantica: json[i].similitud_semantica
    });
    for (let j = 0; j < json[i].historias.length; j++) {
      nodeDataArray = nodeDataArray.concat({
        group: json[i].id,
        key: json[i].historias[j].id
      });
      for (let k = 0; k < json[i].historias[j].dependencias.length; k++) {
        linkDataArray = linkDataArray.concat({
          from: json[i].id,
          to: (function () {
            for (let l = 0; l < json.length; l++) {
              for (let m = 0; m < json[l].historias.length; m++) {
                if (json[i].historias[j].dependencias[k] = json[l].historias[m].id) {
                  return json[l].id;
                }
              }
            }
          })()
        });
      }
    }
  };
  return (
    <div>
      ...
      <ReactDiagram
        initDiagram={initDiagram}
        divClassName='myDiagramDiv'




      />
      ...
    </div>
  );
}

export default App;
