function validarJson(schema, jsonCargado) {
  var Ajv = require("ajv");

  var ajv = new Ajv({
    allErrors: true
  });
  var validate = ajv.compile(schema);
  var valid = validate(jsonCargado);

  if (!valid) {
    console.log(validate.errors);
    swal({
      title: "Invalid schema",
      icon: "error",
      timer: 2000
    });
    return false;
  } else {
    return true;
  }
}

export function leerArchivo(e) {
  var archivo = e.target.files[0];

  if (!archivo) {
    return;
  }

  if (diagramaCargado) {
    myDiagram.div = null;
    myDiagram = null;
  }

  json = [];
  var lector = new FileReader();

  lector.onload = function (e) {
    var jsonData = e.target.result;
    json = JSON.parse(jsonData);

    var schema = require('./schemas/microservices.json');

    var esValido = validarJson(schema, json);

    if (esValido) {
      convertir_jsonAnodos();
    }
  };

  lector.readAsText(archivo);
  document.getElementById("miInput").value = null;
}

function formatearDependencias() {
  // Quita los nombres de las dependencias y las
  var dependencias = []; // deja como un arreglo de strings (historias)

  for (var i = 0; i < userStory.length; i++) {
    dependencias = [];

    for (var j = 0; j < userStory[i].dependencies.length; j++) {
      dependencias = dependencias.concat(userStory[i].dependencies[j].id);
    }

    userStory[i].dependencies = dependencias;
  }
}

function verificarHistoria() {
  // Verifica la existencia de una historia de usuario 
  // en un diagrama de microservicios
  for (var h = 0; h < userStory.length; h++) {
    for (var i = 0; i < json.length; i++) {
      for (var j = 0; j < json[i].userStories.length; j++) {
        if (json[i].userStories[j].id === userStory[h].id) {
          swal({
            title: "The story " + userStory[h].id + " is already in the project",
            text: "Operation cancelled",
            icon: "warning"
          });
          return true;
        }
      }
    }
  }

  formatearDependencias();
  swal({
    title: "Stories included succesfully",
    icon: "success",
    timer: 1500
  });
  return false;
}

function quitarGuionesMedios(us) {
  for (var i = 0; i < us.length; i++) {
    us[i].id = us[i].id.replace("-", "");

    for (var j = 0; j < us[i].dependencies.length; j++) {
      us[i].dependencies[j].id = us[i].dependencies[j].id.replace("-", "");
    }
  }

  return us;
}

function agregarHistoria(e) {
  if (diagramaCargado) {
    var archivo = e.target.files[0];

    if (!archivo) {
      return;
    }

    var lector = new FileReader();

    lector.onload = function (e) {
      var userStoryData = e.target.result;
      var file = JSON.parse(userStoryData);
      userStory = file.userStories;

      var schema = require('./schemas/userStories.json');

      var esValido = validarJson(schema, userStory);

      if (esValido) {
        userStory = quitarGuionesMedios(userStory);
        var existeHistoria = verificarHistoria();

        if (!existeHistoria) {
          var micro_servicio = nuevoMSid();
          crearMicroservicio(micro_servicio, userStory);
          nuevoDiagrama();
        }
      }
    };

    lector.readAsText(archivo);
  } else {
    swal({
      title: "Cargue un diagrama primero",
      icon: "error",
      timer: 2000
    });
  }

  document.getElementById("miInput2").value = null;
}

function exportarJson() {
  if (diagramaCargado) {
    var dataStr = JSON.stringify(json);
    var dataUri = 'data:application/json;charset=utf-8,' + encodeURIComponent(dataStr);
    var exportFileDefaultName = 'data.json'; // Asigna un nombre al archivo

    var linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  } else {
    swal({
      title: "Cargue un diagrama primero",
      icon: "error",
      timer: 2000
    });
  }
}

function nuevoDiagrama() {
  if (diagramaCargado === true) {
    myDiagram.div = null;
    myDiagram = null;
  }

  convertir_jsonAnodos();
  diagramaCargado = true;
} // render function...


