    var $ = go.GraphObject.make;  // Para abreviar la definición de plantillas

    var myToolTip = $(go.HTMLInfo, {
        show: ShowToolTip,  // No hacer nada al ocultar: esta información sobre herramientas no 
    })                      //se oculta a menos que el mouse abandone el diagrama

