        "undoManager.isEnabled": true, // Para hacer debug en consola del navegador
        contentAlignment: go.Spot.Center,  // Centra el contenido en el viewport (ventana gráfica)
        "toolManager.hoverDelay": 10,  // Qué tan rápido aparece el tooltip
        isReadOnly: false,  // No permite modificar
