    });

    let nodos = [];
    let aristas = [];

    for (let i = 0; i < json.length; i++) {
        nodos = nodos.concat({
            acoplamiento_AIS: json[i].acoplamiento.AIS,
            acoplamiento_ADS: json[i].acoplamiento.ADS,
            acoplamiento_SIY: json[i].acoplamiento.SIY,
            cohesion_lack: json[i].cohesion.lack,
            cohesion_grade: json[i].cohesion.grade,
            columnas: Math.ceil(Math.sqrt(json[i].historias.length)),
            complejidad: json[i].complejidad,
            isGroup: true,
            key: json[i].id,
            puntos_historia: json[i].puntos_historia,
            size: 55 + (json[i].historias.length * 12),
            similitud_semantica: json[i].similitud_semantica
        });
        for (let j = 0; j < json[i].historias.length; j++) {
            nodos = nodos.concat({
                group: json[i].id,
                key: json[i].historias[j].id
            });
            for (let k = 0; k < json[i].historias[j].dependencias.length; k++) {
                aristas = aristas.concat({
                    from: json[i].id,
                    to: (function () {
                        for (let l = 0; l < json.length; l++) {
                            for (let m = 0; m < json[l].historias.length; m++) {
                                if (json[i].historias[j].dependencias[k] === json[l].historias[m].id) {
                                    return json[l].id;
                                }
                            }
                        }
                    })()
                });
            }
        }
    };

    myDiagram.groupTemplate = // Define una plantilla simple de grupos
        $(go.Group, "Vertical", { // 'Vertical' Pone el id del nodo abajo
            selectable: true,
            deletable: false, // No permite borrar el grupo
            toolTip: myToolTip,
            copyable: false
        },
