                }
                if (lastStroked !== null && lastStroked !== shape) {
                    lastStroked.stroke = null; // Quita el borde del elemento que ya no está seleccionado
                }
                lastStroked = shape;
                updateInfoBox(e.viewPoint, node.data);
            } else {
                if (lastStroked !== null) {
                    lastStroked.stroke = null;
                }
                lastStroked = null;
                document.getElementById("infoBoxHolder").innerHTML = "";
            }
        }

        // Se asegura de que el cuadro de información esté oculto si el usuario intenta pasar el mouse sobre él
        var infoBoxH = document.getElementById("infoBoxHolder");
        infoBoxH.addEventListener("mousemove", () => {
            var box = document.getElementById("infoBoxHolder");
            box.style.left = parseInt(box.style.left)/* + "px"*/;
            box.style.top = parseInt(box.style.top)/* + 30 + "px"*/;
        }, false);

        var diagramDiv = document.getElementById("myDiagramDiv");

        // Se asegura de que el cuadro de información esté oculto cuando el mouse no esté sobre el Diagrama
        diagramDiv.addEventListener("mouseout", e => {
            if (lastStroked !== null) lastStroked.stroke = null;
            lastStroked = null;
            var infoBox = document.getElementById("infoBox");
            var elem = document.elementFromPoint(e.clientX, e.clientY);
            if (elem !== null && (elem === infoBox || elem.parentNode === infoBox)) {
                var box = document.getElementById("infoBoxHolder");
                box.style.left = parseInt(box.style.left) + "px";
                box.style.top = parseInt(box.style.top) + 30 + "px";
            } else {
                var box = document.getElementById("infoBoxHolder");
                box.innerHTML = "";
            }
        }, false);

    } // end init  

    // Función que actualiza la información en el tooltip
    function updateInfoBox(mousePt, data) {
        var box = document.getElementById("infoBoxHolder");
        box.innerHTML = "";
        var infobox = document.createElement("div");
        infobox.id = "infoBox";
        box.appendChild(infobox);
        for (var i = 0; i < 17; i++) {
            var child = document.createElement("div");
            switch (i) {
                case 0: child.className = "infoTitle"; child.textContent = data.key; break;
                case 1: child.className = "infoKey"; child.textContent = "Acoplamiento (AIS)"; break;
                case 2: child.className = "infoValue"; child.textContent = data.acoplamiento_AIS; break;
                case 3: child.className = "infoKey"; child.textContent = "Acoplamiento (ADS)"; break;
                case 4: child.className = "infoValue"; child.textContent = data.acoplamiento_ADS; break;
                case 5: child.className = "infoKey"; child.textContent = "Acoplamiento (SIY)"; break;
                case 6: child.className = "infoValue"; child.textContent = data.acoplamiento_SIY; break;
                case 7: child.className = "infoKey"; child.textContent = "Cohesión (Lack)"; break;
                case 8: child.className = "infoValue"; child.textContent = data.cohesion_lack; break;
                case 9: child.className = "infoKey"; child.textContent = "Cohesión (Grade)"; break;
                case 10: child.className = "infoValue"; child.textContent = data.cohesion_grade; break;
                case 11: child.className = "infoKey"; child.textContent = "Complejidad cognitiva"; break;
                case 12: child.className = "infoValue"; child.textContent = data.complejidad; break;
                case 13: child.className = "infoKey"; child.textContent = "Similitud semántica"; break;
                case 14: child.className = "infoValue"; child.textContent = data.similitud_semantica; break;
                case 15: child.className = "infoKey"; child.textContent = "Puntos de historia"; break;
                case 16: child.className = "infoValue"; child.textContent = data.puntos_historia; break;
            }
            infobox.appendChild(child);
        }
        box.style.left = mousePt.x + 30 + "px";
        box.style.top = mousePt.y + 20 + "px";
    }
    window.addEventListener('DOMContentLoaded', init);
}
