    for (let j = 0; j < json[i].userStories.length; j++) {
      nodos = nodos.concat({
        group: json[i].id,
        key: json[i].userStories[j].id
      });
      for (let k = 0; k < json[i].userStories[j].dependencies.length; k++) {
        aristas = aristas.concat({
          from: json[i].id,
          to: (function () {
            for (let l = 0; l < json.length; l++) {
              for (let m = 0; m < json[l].userStories.length; m++) {
                if (json[i].userStories[j].dependencies[k] === json[l].userStories[m].id) {
                  return json[l].id;
                }
              }
            }
          })()
        });
      }
    }
  };

  myDiagram.groupTemplate = // Define una plantilla simple de grupos
    $(go.Group, "Vertical", { // 'Vertical' Pone el id del nodo abajo
      selectable: true,
      deletable: false, // No permite borrar el grupo
      toolTip: myToolTip,
      copyable: false
    },
      new go.Binding("layout", "columnas", function (c) {
        return $(go.GridLayout, {
          wrappingColumn: c, // Determina cantidad de columnas
          cellSize: new go.Size(0, 0), // Determina ancho de columnas
          spacing: new go.Size(0, 0) // Determina espacio entre columnas
        });
      }),
      $(go.Panel, "Spot",
        $(go.Shape, "Hexagon", {
          name: "MICROSERVICIO",
          strokeWidth: 4, // Ancho del borde
          stroke: null, // Sin borde
          fill: "rgba(125, 200, 120, .6)"
        },
          new go.Binding("width", "size"), // Asigna al ancho el valor de size
          new go.Binding("height", "size") // Asigna a la altura el valor de size
        ), $(go.TextBlock, "text", {
          alignment: new go.Spot(0.5, 0.23) // valores de 0,0 (izquierda,arriba) a 1,1 (derecha, abajo)
        },
          new go.Binding("text", "key") // Asigna al texto el valor de key
        ),
        $(go.Placeholder, { // Centra las HUs en el hexágono del microservicio
          padding: 1
        })
      )
    );

  myDiagram.nodeTemplate =
    $(go.Node, "Auto", { // 'Auto' Ajusta el tamaño del elemento según sus subelementos. 
      //locationSpot: go.Spot.Center,
      selectable: false,
      deletable: false // No permite borrar el nodo
    },
      $(go.Shape, "RoundedRectangle", {
        fill: "white"
      }),
      $(go.TextBlock, "text",
        new go.Binding("text", "key") // Asigna al texto el valor de key
      )
    );

  myDiagram.linkTemplate =
    $(go.Link, {
      curve: go.Link.Bezier,
      reshapable: true,
      deletable: false // No permite borrar la flecha
    },
      // No necesita guardar Link.points, por lo que no necesita TwoWay Binding en "puntos"
      new go.Binding("curviness", "curviness").makeTwoWay(),  // pero guarda "curvas" automáticamente
      $(go.Shape, { // Características del cuerpo de la flecha       
        strokeWidth: 2
      }),
      $(go.Shape, { // Características de la cabeza de la flecha  
        toArrow: "standard", // Forma de la cabeza de la flecha  
        stroke: "white" // Borde de la cabeza de flecha
      })
    );

  myDiagram.model = new go.GraphLinksModel(nodos, aristas); // Crea el modelo del diagrama a mostrar

  function doMouseOver(e) { // Llamado cuando el mouse está sobre el fondo del diagrama
    if (e === undefined) {
      e = myDiagram.lastInput;
    }
    var doc = e.documentPoint;

    // Encuentra todos los nodos que están a 1 unidad de distancia
    var list = myDiagram.findObjectsNear(doc, 1, null, x => x instanceof go.Node);

    // Encuentra el que está más cerca de e.documentPoint
    var closest = null;
    var closestDist = 999999999;
    list.each(node => {
      var dist = doc.distanceSquaredPoint(node.getDocumentPoint(go.Spot.Center));
      if (dist < closestDist) {
        closestDist = dist;
        closest = node;
      }
    });
    showToolTip(closest, myDiagram);
  }

  function showToolTip(obj, diagram) { // Llamado con un Nodo cerca o sobre el que 
    if (obj !== null) {
      var node = obj.part;
      var e = diagram.lastInput;
      var shape = node.findObject("MICROSERVICIO");
      if (shape != null) {
        shape.stroke = "white"; // Pone borde al elemento seleccionado
      }
      if (lastStroked !== null && lastStroked !== shape) {
        lastStroked.stroke = null; // Quita el borde del elemento que ya no está seleccionado
      }
      lastStroked = shape;
      updateInfoBox(e.viewPoint, node.data);
    } else {
      if (lastStroked !== null) {
        lastStroked.stroke = null;
      }
      lastStroked = null;
      document.getElementById("infoBoxHolder").innerHTML = "";
    }
  }

  // Se asegura de que el cuadro de información esté oculto si el usuario intenta pasar el mouse sobre él
  var infoBoxH = document.getElementById("infoBoxHolder");
  infoBoxH.addEventListener("mousemove", () => {
    var box = document.getElementById("infoBoxHolder");
    box.style.left = parseInt(box.style.left)/* + "px"*/;
    box.style.top = parseInt(box.style.top)/* + 30 + "px"*/;
  }, false);

  var diagramDiv = document.getElementById("myDiagramDiv");

  // Se asegura de que el cuadro de información esté oculto cuando el mouse no esté sobre el Diagrama
  diagramDiv.addEventListener("mouseout", e => {
    if (lastStroked !== null) lastStroked.stroke = null;
    lastStroked = null;
    var infoBox = document.getElementById("infoBox");
    var box = document.getElementById("infoBoxHolder");
    var elem = document.elementFromPoint(e.clientX, e.clientY);
    if (elem !== null && (elem === infoBox || elem.parentNode === infoBox)) {
      box.style.left = parseInt(box.style.left) + "px";
      box.style.top = parseInt(box.style.top) + 30 + "px";
    } else {
      box.innerHTML = "";
    }
  }, false);
} // end init

// Función que actualiza la información en el tooltip
function updateInfoBox(mousePt, data) {
  var box = document.getElementById("infoBoxHolder");
  box.innerHTML = "";
  var infobox = document.createElement("div");
  infobox.id = "infoBox";
  box.appendChild(infobox);
  for (var i = 0; i < 17; i++) {
    var child = document.createElement("div");
    switch (i) {
      case 0: child.className = "infoTitle"; child.textContent = data.key; break;
      case 1: child.className = "infoKey"; child.textContent = "Acoplamiento (AIS)"; break;
      case 2: child.className = "infoValue"; child.textContent = data.acoplamiento_AIS; break;
      case 3: child.className = "infoKey"; child.textContent = "Acoplamiento (ADS)"; break;
      case 4: child.className = "infoValue"; child.textContent = data.acoplamiento_ADS; break;
      case 5: child.className = "infoKey"; child.textContent = "Acoplamiento (SIY)"; break;
      case 6: child.className = "infoValue"; child.textContent = data.acoplamiento_SIY; break;
      case 7: child.className = "infoKey"; child.textContent = "Cohesión (Lack)"; break;
      case 8: child.className = "infoValue"; child.textContent = data.cohesion_lack; break;
      case 9: child.className = "infoKey"; child.textContent = "Cohesión (Grade)"; break;
      case 10: child.className = "infoValue"; child.textContent = data.cohesion_grade; break;
      case 11: child.className = "infoKey"; child.textContent = "Complejidad cognitiva"; break;
      case 12: child.className = "infoValue"; child.textContent = data.complejidad; break;
      case 13: child.className = "infoKey"; child.textContent = "Similitud semántica"; break;
      case 14: child.className = "infoValue"; child.textContent = data.similitud_semantica; break;
      case 15: child.className = "infoKey"; child.textContent = "Puntos de historia"; break;
      case 16: child.className = "infoValue"; child.textContent = data.puntos_historia; break;
      default: child.textContent = ""; break;
    }
    infobox.appendChild(child);
  }
  box.style.left = mousePt.x + 30 + "px";
  box.style.top = mousePt.y + 20 + "px";
}
window.addEventListener('DOMContentLoaded', init);

export default App;
