export function LeerArchivo(e) {
  if (diagramaCargado === true) {
    myDiagram.div = null;
    myDiagram = null;
  }
  var archivo = e.target.files[0];
  if (!archivo) {
    return;
  }
  var lector = new FileReader();
  lector.onload = function (e) {
    var jsonData = e.target.result;
    //mostrarContenido(jsonData);
    json = JSON.parse(jsonData)
    convertir_jsonAnodos(json);
    diagramaCargado = true;
  };
  lector.readAsText(archivo);
}

function formatearDependencias() { // Quita los nombres de las dependencias y las
  var dependencias = [];     // deja como un arreglo de strings (historias)
  for (let i = 0; i < userStory[0].dependencies.length; i++) {
    dependencias = dependencias.concat(userStory[0].dependencies[i].id);
  }
  userStory[0].dependencies = dependencias;
  console.log(userStory);
}

function verificarHistoria() {   // Verifica la existencia de una historia de usuario 
  var story = userStory[0].id; // en un diagrama de microservicios
  for (let i = 0; i < json.length; i++) {
    for (let j = 0; j < json[i].userStories.length; j++) {
      if (json[i].userStories[j].id == story) {
        alert("Este id de historia ya existe");
        return true;
      }
    }
  }
  formatearDependencias();
  return false;
}

function agregarHistoria() {
  if (diagramaCargado) {
    var existeHistoria = verificarHistoria();
    if (!existeHistoria) {
      let micro_servicio = prompt("En qué microservicio?");
      for (let i = 0; i < json.length; i++) {
        if (json[i].id === micro_servicio) {
          json[i].userStories = json[i].userStories.concat(userStory);
          nuevoDiagrama();
          break;
        }
      }
    }
  } else {
    alert("Cargue un diagrama primero.")
  }
}

function nuevoDiagrama() {
  if (diagramaCargado === true) {
    myDiagram.div = null;
    myDiagram = null;
  }
  convertir_jsonAnodos(json);
  diagramaCargado = true;
}

